jsLib:
function openUrl(e, n) {
	  const { java } = this
   var Data = java.base64DecodeToByteArray(e);
   var numBytes = java.base64DecodeToByteArray(n);
   var numStr = java.bytesToStr(numBytes);
   var ua = "mozilla/5.0 (linux; android 9) mobile safari/537.36";
   var keyMd5 = java.md5Encode(ua + numStr)
   var key = java.strToBytes(keyMd5);
return java.createSymmetricCrypto(
   "AES/CBC/PKCS7Padding", 
   key, 
   Data.slice(0, 16)
).decryptStr(Data.slice(16));
}

